import React, { useState } from 'react';

export default function Admin() {
    const [posts, setPosts] = useState([]);
    const [currentPost, setCurrentPost] = useState({
        title: '',
        content: '',
        type: 'festival', // festival, restaurant, curation
        author: 'Admin',
        createdAt: new Date().toISOString()
    });
    const [isEditing, setIsEditing] = useState(false);
    const [editingId, setEditingId] = useState(null);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setCurrentPost(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        
        if (isEditing) {
            // 수정 모드
            setPosts(prev => prev.map(post => 
                post.id === editingId 
                    ? { ...currentPost, id: editingId, updatedAt: new Date().toISOString() }
                    : post
            ));
            setIsEditing(false);
            setEditingId(null);
        } else {
            // 새 글 작성
            const newPost = {
                ...currentPost,
                id: Date.now(),
                createdAt: new Date().toISOString()
            };
            setPosts(prev => [newPost, ...prev]);
        }
        
        // 폼 초기화
        setCurrentPost({
            title: '',
            content: '',
            type: 'festival',
            author: 'Admin',
            createdAt: new Date().toISOString()
        });
    };

    const handleEdit = (post) => {
        setCurrentPost({
            title: post.title,
            content: post.content,
            type: post.type,
            author: post.author,
            createdAt: post.createdAt
        });
        setIsEditing(true);
        setEditingId(post.id);
    };

    const handleDelete = (id) => {
        if (window.confirm('정말로 이 글을 삭제하시겠습니까?')) {
            setPosts(prev => prev.filter(post => post.id !== id));
        }
    };

    const getTypeLabel = (type) => {
        const typeLabels = {
            festival: '축제',
            restaurant: '맛집',
            curation: '큐레이션'
        };
        return typeLabels[type] || type;
    };

    const getTypeColor = (type) => {
        const typeColors = {
            festival: 'bg-yellow-100 text-yellow-800',
            restaurant: 'bg-red-100 text-red-800',
            curation: 'bg-blue-100 text-blue-800'
        };
        return typeColors[type] || 'bg-gray-100 text-gray-800';
    };

    return (
        <div className="p-6">
            <h1 className="text-3xl font-bold text-gray-800 mb-8">관리자 글 작성</h1>
            
            {/* 글 작성 폼 */}
            <div className="bg-white rounded-lg shadow-md p-6 mb-8">
                <h2 className="text-xl font-semibold text-gray-700 mb-4">
                    {isEditing ? '글 수정하기' : '새 글 작성하기'}
                </h2>
                
                <form onSubmit={handleSubmit} className="space-y-4">
                    {/* 제목 입력 */}
                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2">
                            제목
                        </label>
                        <input
                            type="text"
                            name="title"
                            value={currentPost.title}
                            onChange={handleInputChange}
                            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                            placeholder="글 제목을 입력하세요"
                            required
                        />
                    </div>
                    
                    {/* 타입 선택 */}
                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2">
                            카테고리
                        </label>
                        <select
                            name="type"
                            value={currentPost.type}
                            onChange={handleInputChange}
                            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                        >
                            <option value="festival">축제</option>
                            <option value="restaurant">맛집</option>
                            <option value="curation">큐레이션</option>
                        </select>
                    </div>
                    
                    {/* 내용 입력 */}
                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2">
                            내용
                        </label>
                        <textarea
                            name="content"
                            value={currentPost.content}
                            onChange={handleInputChange}
                            rows="6"
                            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
                            placeholder="글 내용을 입력하세요"
                            required
                        />
                    </div>
                    
                    {/* 버튼 */}
                    <div className="flex gap-3">
                        <button
                            type="submit"
                            className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition-colors"
                        >
                            {isEditing ? '수정하기' : '작성하기'}
                        </button>
                        
                        {isEditing && (
                            <button
                                type="button"
                                onClick={() => {
                                    setIsEditing(false);
                                    setEditingId(null);
                                    setCurrentPost({
                                        title: '',
                                        content: '',
                                        type: 'festival',
                                        author: 'Admin',
                                        createdAt: new Date().toISOString()
                                    });
                                }}
                                className="px-6 py-2 bg-gray-500 text-white rounded-md hover:bg-gray-600 focus:outline-none focus:ring-2 focus:ring-gray-500 focus:ring-offset-2 transition-colors"
                            >
                                취소
                            </button>
                        )}
                    </div>
                </form>
            </div>
            
            {/* 작성된 글 목록 */}
            <div className="bg-white rounded-lg shadow-md p-6">
                <h2 className="text-xl font-semibold text-gray-700 mb-4">작성된 글 목록</h2>
                
                {posts.length === 0 ? (
                    <div className="text-center py-8 text-gray-500">
                        작성된 글이 없습니다.
                    </div>
                ) : (
                    <div className="space-y-4">
                        {posts.map(post => (
                            <div key={post.id} className="border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow">
                                <div className="flex items-start justify-between mb-3">
                                    <div className="flex-1">
                                        <div className="flex items-center gap-3 mb-2">
                                            <span className={`px-2 py-1 rounded-full text-xs font-medium ${getTypeColor(post.type)}`}>
                                                {getTypeLabel(post.type)}
                                            </span>
                                            <h3 className="text-lg font-semibold text-gray-800">{post.title}</h3>
                                        </div>
                                        <p className="text-gray-600 text-sm mb-2">
                                            작성자: {post.author} | 
                                            작성일: {new Date(post.createdAt).toLocaleDateString('ko-KR')}
                                            {post.updatedAt && ` | 수정일: ${new Date(post.updatedAt).toLocaleDateString('ko-KR')}`}
                                        </p>
                                        <p className="text-gray-700 leading-relaxed">
                                            {post.content.length > 100 
                                                ? `${post.content.substring(0, 100)}...` 
                                                : post.content
                                            }
                                        </p>
                                    </div>
                                    
                                    <div className="flex gap-2 ml-4">
                                        <button
                                            onClick={() => handleEdit(post)}
                                            className="px-3 py-1 bg-yellow-500 text-white text-sm rounded hover:bg-yellow-600 transition-colors"
                                        >
                                            수정
                                        </button>
                                        <button
                                            onClick={() => handleDelete(post.id)}
                                            className="px-3 py-1 bg-red-500 text-white text-sm rounded hover:bg-red-600 transition-colors"
                                        >
                                            삭제
                                        </button>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
}
